mapper.select:
select products.hotel_id hotelId, products.hotel_name hotelName, products.active offShelveStatus, products.room_id roomId, products.room_name roomName, products.product_id productId, products.product_name productName, products.advance_payment_id advancePaymentId, products.breakfast_qty breakfastQty, products.supplier_code supplierCode, products.org_name supplierName, products.roomBedTypes roomBedTypes, products.b2b_sale_status b2bSaleStatus, products.b2c_sale_status b2cSaleStatus, products.ctrip_sale_status ctripSaleStatus, products.feizhu_sale_status feizhuSaleStatus, products.qunar_sale_status qunarSaleStatus, products.tcyl_sale_status tcylSaleStatus, products.meituan_sale_status meituanSaleStatus, products.bed_types bedTypes, products.purchase_type purchaseType, products.currency currency, quotations.sale_date saleDate, quotations.base_price basePrice, quotations.over_draft_status overDraftStatus, quotations.quota quota, quotations.remaining_quota remainingQuota, quotations.room_status roomStatus from (SELECT h.hotel_id, h.hotel_name, r.room_id, r.room_name, r.bed_types roomBedTypes, p.product_id, p.product_name, p.advance_payment_id, p.breakfast_qty, p.supplier_code, p.bed_types, p.purchase_type, p.currency, p.active, o.org_name, ps.b2b_sale_status, ps.b2c_sale_status, ps.ctrip_sale_status, ps.feizhu_sale_status, ps.qunar_sale_status, ps.tcyl_sale_status, ps.meituan_sale_status FROM t_baseinfo_hotel h, t_baseinfo_room r, t_pro_product p, t_pro_sale_status ps, t_org_company_supplier cs, t_org_organization o where h.hotel_id = p.hotel_id and r.room_id = p.room_id and p.product_id = ps.product_id and o.org_code = p.supplier_code and cs.company_code = ps.company_code and cs.org_id = o.org_id and cs.available_status = 1 and ps.company_code = #{companyCode} and p.hotel_id = #{hotelId}
and p.supplier_code = #{supplierCode}
and p.purchase_type = #{purchaseType}
and ps.active = 1) products left join (SELECT saledate.product_id, saledate.sale_date, price.base_price, quota.over_draft_status, quota.quota, quota.remaining_quota, quota.room_status FROM ( SELECT sd.sale_date, p.product_id FROM t_pro_sale_date sd, t_pro_product p WHERE sd.sale_date >= #{startDate} AND sd.sale_date <= #{endDate} AND p.hotel_id = #{hotelId} ) saledate LEFT JOIN ( SELECT p.product_id, pi.sale_date, pi.base_price FROM t_pro_product p, t_pro_price pi WHERE p.product_id = pi.product_id AND p.hotel_id = #{hotelId} AND pi.sale_date >= #{startDate} AND pi.sale_date <= #{endDate} ) price ON saledate.sale_date = price.sale_date AND saledate.product_id = price.product_id LEFT JOIN ( SELECT p.product_id, q.over_draft_status, q.quota, q.remaining_quota, q.room_status, q.sale_date FROM t_pro_product p, t_pro_quota q WHERE p.quota_account_id = q.quota_account_id AND p.hotel_id = #{hotelId} AND q.sale_date >= #{startDate} AND q.sale_date <= #{endDate} ) quota ON saledate.sale_date = quota.sale_date AND saledate.product_id = quota.product_id ) quotations on products.product_id = quotations.product_id

SELECT
  p.content content,
  p.created_dt createdDt,
  p.created_by createdBy,
  p.operation_week operationWeek
FROM t_pro_sale_log p
WHERE p.product_id = #{productId}
     AND p.operation_type = #{operationType} AND p.company_code = #{companyCode}

    AND p.start_date <= #{saleDate} AND p.end_date >= #{saleDate}


    AND p.channel_code = #{channelCode}

ORDER BY p.id DESC
